SELECT districts.name, expenditures.per_pupil_expenditure, staff_evaluations.exemplary FROM "districts" JOIN "expenditures" ON districts.id = expenditures.district_id JOIN staff_evaluations ON districts.id = staff_evaluations.district_id WHERE districts.type = 'Public School District' AND districts.id IN (SELECT * FROM (SELECT district_id FROM "staff_evaluations" WHERE exemplary > (SELECT AVG(exemplary) FROM "staff_evaluations")) INTERSECT SELECT * FROM (SELECT district_id FROM "expenditures" WHERE per_pupil_expenditure > (SELECT AVG(per_pupil_expenditure) FROM "expenditures")))  ORDER BY staff_evaluations.exemplary DESC, per_pupil_expenditure DESC;
